import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";
import "./ExpenseList.css";

export default function ExpenseList(props) {
  const [filterYear, setFilterYear] = useState(new Date());
  const setFilterYearHandler = (year) => {
    console.log("In ExpenseList");
    console.log(year);
    setFilterYear(year);
  };

  const filteredExpenses = props.expenses.filter(expense => expense.date.getYear() === filterYear);

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selectedYear={filterYear}
          onFilterYearChange={setFilterYearHandler}
        />
        {/* Populate this ExpenseList with each expense transformed into an ExpenseItem */}
        {filteredExpenses.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
}
