import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";
import "./ExpenseList.css";

export default function ExpenseList(props) {
  const [filterYear, setFilterYear] = useState(new Date());
  const setFilterYearHandler = (year) => {
    console.log("In ExpenseList");
    console.log(year);
    setFilterYear(year);
  };

  // Filter props.expenses down to the year matching the ExpensesFilter's
  const filteredExpenses = props.expenses.filter(
    (expense) => expense.date.getFullYear().toString() === filterYear
  );

  let expensesContent = <p>No expenses found.</p>;
  if (filteredExpenses.length > 0) {
    expensesContent = filteredExpenses.map((expense) => (
      <ExpenseItem
        key={expense.id}
        title={expense.title}
        amount={expense.amount}
        date={expense.date}
      />
    ));
  }

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selectedYear={filterYear}
          onFilterYearChange={setFilterYearHandler}
        />
        {/* {filteredExpenses.length === 0 && <p>No expenses found.</p>} */}
        {/* Only populate this ExpenseList if expenses are present by the ExpensesFilter */}
        {filteredExpenses.length > 0 && 
        /* Populate this ExpenseList with each expense transformed into an ExpenseItem */
        filteredExpenses.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
}
